	if ( ! m_isContentTruncatedValid     ) { char *xx=NULL;*xx=0; }
	if ( ! m_isLinkSpamValid             ) { char *xx=NULL;*xx=0; }

	// buffers
	if ( ! m_firstUrlValid               ) { char *xx=NULL;*xx=0; }
	if ( ! m_redirUrlValid               ) { char *xx=NULL;*xx=0; }
	//if ( ! m_metaRedirUrlValid           ) { char *xx=NULL;*xx=0; }
