// this means any tod now
bool *XmlDoc::getHasTOD ( ) {
	if ( m_hasTODValid ) return &m_hasTOD2;
	// scan the dates
	Dates *dp = getDates() ;
	if ( ! dp || dp == (Dates *)-1 ) return (bool *)dp;
	// assume not
	m_hasTOD2 = false;
	m_hasTOD  = false;
	// scan the dates
	for ( long i = 0 ; i < dp->m_numDatePtrs ; i++ ) {
		// breathe
		QUICKPOLL ( m_niceness );
		// get date
		Date *di = dp->m_datePtrs[i];
		// skip if got nuked
		if ( ! di ) continue;
		// tod?
		if ( !(di->m_hasType & DT_TOD) ) continue;
		// got one
		m_hasTOD2 = true;
		m_hasTOD  = true;
	}
	// it is now valid
	m_hasTODValid = true;
	return &m_hasTOD2;
}

