	// get this
	//TagRec *gr = (TagRec *)ptr_tagRecData;
	//Tag    *tag = NULL;
	//if ( gr ) tag = gr->getTag("sitenuminlinks");
	// reset
	sreq->reset();
	// assume not valid
	sreq->m_siteNumInlinks = -1;

	if ( ! m_siteNumInlinksValid ) { char *xx=NULL;*xx=0; }
	// how many site inlinks?
	sreq->m_siteNumInlinks       = m_siteNumInlinks;
