	if ( ! strncmp ( host , "youtube.com/"      , 12 ) ) found = 1;
	if ( ! strncmp ( host , "www.metacafe.com/" , 17 ) ) found = 2;
	if ( ! strncmp ( host , "metacafe.com/"     , 13 ) ) found = 2;
