		long tmpLen = sprintf(tmp,"%02li%%",percentLong);
		if ( tmpLen !=3)log("seo: bad progress bar output %li",tmpLen);
		// try a send on non-blocking socket
		long n = ::send ( m_seoSocket->m_sd , tmp,tmpLen , 0 );
